import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Character } from '@app/shared/interface/character.interface';
import { CharacterService } from '@app/shared/services/character.service';
import { Observable, take } from 'rxjs';
import {Location}  from '@angular/common'

@Component({
  selector: 'app-character-details',
  templateUrl: './character-details.component.html',
  styleUrls: ['./character-details.component.scss']
})
export class CharacterDetailsComponent implements OnInit {
  character$!: Observable<Character>;
  constructor(private route:ActivatedRoute, private characterSvc: CharacterService, private location: Location) { }

  ngOnInit(): void {
    this.route.params.pipe(take(1)).subscribe((params)=>{
      const id = params['id'];
      this.character$ = this.characterSvc.getDetails(id);
    });
  }

  onBack():void{
    this.location.back();
  }

}
<div class="row justify-content-center" *ngIf="character$ | async as character">
  <div class="col-xs-12 col-sm-6 col-md-6 justify-content-center">
    <div class="card">
      <div class="image">
          <img
            [src]="character.image"
            [alt]="character.name"
            class="card-img-top"
            />
      </div>
       <div class="car-inner">
          <div class="header" style="display: block;">
              <h2>{{character.name}}</h2>
            <div class="pointText">
              <h4 class="text-muted">{{character.gender}}</h4>
              <h5 class="text-muted ml-2">{{character.species}}</h5>
            </div>
            <div class="pointText">
              <div class="point" [ngClass]="character.status === 'Alive' ? 'Alive' : (character.status === 'Dead' ? 'Dead' : 'unknown')"></div>

              <h3 class="text-muted">{{character.status}}</h3>
            </div>
            <h5 class="text-muted">{{character.species}}</h5>
            <small class="text-muted" style="display: block;">{{character.created | date}}</small>
          </div>
       </div>
    </div>
  </div>
</div>
<button (click)="onBack()" class="btn btn-primary btn-lg mt-3"
>BACK</button>
